#point mutation calculation

s = ''
t = ''

def cnt_mutation(s, t):
    l = min(len(s), len(t))
    result = abs(len(s) - len(t))
    for i in range(0, l):
        if s[i] != t[i]:
            result += 1
    return print("The humming distance (amount of point mutation) is "+ str(result))


cnt_mutation(s,t)

